import React, { Component } from 'react';
import Cookies from 'js-cookie';
import {request, requests, uploadFiles, getToken} from './requests';

class LK extends Component {
    constructor (props) {
        super (props);
        this.state = {
            me: {username: '', photo: 'http://dream/profilepictures/0.png'}
        };
        this.update();
    };

    update() {
        requests({
            'getUserInfo': {heys: ['name', 'photo']},
            'getTypes': {},
            'getCategories': {}
        }, {'token': getToken()}).then(res => {
            if (res.success) {
                let data = res.result;
                if (data.getUserInfo)
                    this.setState({
                        me: {
                            username: data.getUserInfo.data.name,
                            photo: data.getUserInfo.data.photo
                        }
                    });
            }
        });
    }
    
    render() {
        return (<>
            <header>
            </header>
            <div id="LK">
            </div>    
        </>)
    }
}

export default LK;
